package ex03_ex04;

public class SignOpertaor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//-음수 -> 양수
		int x = -77;
		x = -x;
		System.out.println("x: " + x);
		
		//-양수 -> 음수
		byte b = 77;
		int y = -b;
		System.out.println("y: " + y);
	}

}
